var dapxApp=angular.module("redditApp");dapxApp.controller("subCtrl",["$scope","$http","$location","subFactory",function(t,a,o,s){var p=o.path().substr(1);t.loading=!0,t.posts=s.getPosts(p).then(function(a){t.loading=!1,t.subName=a[0].data.subreddit,t.posts=a,t.sortPosts=function(a){o.path("popular"==a?t.subName:t.subName+"/"+a)}},function(t){alert(t)})}]);